options.cartocss:
#mars_nomenclature{

          [zoom=1]{
            [diameter>=3000]{
              text-name: [name];
              text-face-name: 'Lato Bold';
              text-fill: #f2c474;
              text-dy: 0;
              text-size: 12;
              text-allow-overlap: true;
              text-placement: point;
              text-placement-type: simple;
              text-transform: uppercase;
              text-wrap-width: 10;
            }
          }

          [zoom=2]{
            [diameter>=3000]{
              text-name: [name];
              text-face-name: 'Lato Bold';
